document.addEventListener('DOMContentLoaded',()=>{const promoContainer=document.querySelector('.promo-container');if(!promoContainer)return;const promoTextSmallLeft=promoContainer.querySelector('.promo-left .promo-text-small');const promoBadge=promoContainer.querySelector('.promo-badge');const promoImage=promoContainer.querySelector('.promo-car-image');const promoTextSmallRight=promoContainer.querySelector('.promo-right .promo-text-small');const promoScript=promoContainer.querySelector('.promo-script');const promoItems=[{leftText:"Get the best",badge:"Facelift KITS",image:"assets/images/hero/hero-1.webp",rightText:"Smooth Motion",script:"Specials"},{leftText:"Upgrade to",badge:"Premium OIL",image:"assets/images/hero/hero-2.webp",rightText:"for your engine",script:"Performance"},{leftText:"Secure your",badge:"Brake PADS",image:"assets/images/hero/hero-3.webp",rightText:"safety first",script:"Reliability"},{leftText:"Ride smooth",badge:"Suspension",image:"assets/images/hero/hero-1.webp",rightText:"on every road",script:"Comfort"}];let currentIndex=0;function updateBanner(){promoContainer.classList.add('fade-out');setTimeout(()=>{currentIndex=(currentIndex+1)% promoItems.length;const item=promoItems[currentIndex];promoTextSmallLeft.textContent=item.leftText;promoBadge.textContent=item.badge;promoImage.src=item.image;promoImage.alt=item.badge;promoTextSmallRight.textContent=item.rightText;promoScript.textContent=item.script;promoContainer.classList.remove('fade-out');},500);}setInterval(updateBanner,4500);});